const handleContent02=()=>({monthUrls:[],async init(){const t=Alpine.store("blogUrls"),n=await fetch("https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/json/month_urls.json"),e=(new Date).getMonth()+1;this.monthUrls=(await n.json()).filter((t=>t.month>=e)).map((n=>({...n,events:t.filter((t=>n.month===Number(dayjs(""+t.scheduleStartDate).format("M")))).map((t=>t.eventName)).join(", ")}))).filter((t=>t.events.trim().length>0))}});replaceWith("content02","https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/ma/sections/content/02.html");