const handleContent03=()=>({regionUrls:[],async init(){const n=Alpine.store("blogUrls"),t=await fetch("https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/json/region_urls.json");this.regionUrls=(await t.json()).map((t=>({...t,events:n.filter((n=>t.region===n.eventRegion)).map((n=>n.eventName)).join(", ")}))).filter((n=>n.events.trim().length>0))}});replaceWith("content03","https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/ma/sections/content/03.html");