const handleContent04=()=>({venueUrls:[],async init(){const e=Alpine.store("blogUrls"),n=await fetch("https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/json/venue_urls.json");this.venueUrls=(await n.json()).map((n=>({...n,events:e.filter((e=>n.venue===e.eventVenue)).map((e=>e.eventName)).join(", ")}))).filter((e=>e.events.trim().length>0))}});replaceWith("content04","https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/ma/sections/content/04.html");