const handleContent04=()=>({venueUrls:[],async init(){const e=Alpine.store("blogUrls"),t=await fetch("https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/json/venue_urls.json?s="+(new Date).getTime());this.venueUrls=(await t.json()).map((t=>({...t,events:e.filter((e=>t.venue===e.eventVenue)).map((e=>e.eventName)).join(", ")}))).filter((e=>e.events.trim().length>0))}});replaceWith("content04","https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/ma/sections/content/04.html");