const handleContent01=()=>({fairs:[],init(){const t=Alpine.store("blogUrls"),e=(new Date).getFullYear()+subDns.replace(/^pa/,"");this.fairs=t.filter((t=>(""+t.scheduleStartDate).substring(0,6)===e)).map((t=>({...t,fromToDt:`${dayjs(""+t.scheduleStartDate).format("YYYY.MM.DD")} ~ ${dayjs(""+t.scheduleEndDate).format("YYYY.MM.DD")}`,preregDt:dayjs(""+t.preregEndDate).format("YYYY.MM.DD")})))}});replaceWith("content01","https://cdn.jsdelivr.net/gh/motungilife/fair@main/dist/pa/month/sections/content/01.html");